export async function get_mapped_task(dagRunId,taskId,mapIndex){
    const username = 'airflow';
    const password = 'airflow';
    const authHeader = 'Basic ' + btoa(`${username}:${password}`);
    const dagId = 'finScraping';

    try {
        const response = await fetch(`http://localhost:5005/api/v1/dags/${dagId}/dagRuns/${dagRunId}/taskInstances/${taskId}/${mapIndex}`, {
            method: 'GET',
            headers: {
                'Authorization': authHeader,
                'Content-Type': 'application/json'
            },
            
        });

        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        return data;
    } catch (err) {
        console.error('Error triggering DAG run:', err);
        return null;
    }
}

const verify_xcom = async (dagRunId, taskId, mapIndex = null) => {
    const username = 'airflow';
    const password = 'airflow';
    const authHeader = 'Basic ' + btoa(`${username}:${password}`);
    const dagId = 'finScraping';

    let url
    if(mapIndex == null){
        url = `http://localhost:5005/api/v1/dags/${dagId}/dagRuns/${dagRunId}/taskInstances/${taskId}/xcomEntries`
    }
    else{
        url = `http://localhost:5005/api/v1/dags/${dagId}/dagRuns/${dagRunId}/taskInstances/${taskId}/xcomEntries?map_index=${mapIndex}`
    }


    try {
        const response = await fetch(url, {
            method: 'GET',
            headers: {
                'Authorization': authHeader,
                'Content-Type': 'application/json'
            },
            
        });

        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        if(data.total_entries > 0){
            return true;
        }
        else{
            return false;
        }
    } catch (err) {
        console.error('Error triggering DAG run:', err);
        return null;
    }
}
