const transform_date = (dateString) => {

    try {
        // Convert the date string to a Date object
        let date = new Date(dateString);

        // Get the timestamp in milliseconds and then convert it to seconds
        let timestampInSeconds = Math.floor(date.getTime() / 1000);

        return (timestampInSeconds);
    }
    catch (error) {
        return undefined
    }
}

export async function fetchPosts(filter, size, page) {

    const start_date = transform_date(filter.start_date) || undefined
    const end_date = transform_date(filter.end_date) || undefined

    if (start_date > end_date) {
        return { 'error': true, 'message': 'The start date must be earlier than the end date' }
    }


    const params = {
        sources: filter.sources || undefined,
        search: filter.search || undefined,
        start_date: start_date,
        end_date: end_date,
        sort: filter.sort || 'By date',
        size: size || 100,
        page: page || 0
    };



    try {
        const response = await axios.post("http://localhost:5001/posts", params);

        return response.data
    } catch (error) {
        console.error("Error fetching sources:", error);
        return null
    }



}



const shuffleArray = (array) => {
    for (let i = array.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1))
            ;[array[i], array[j]] = [array[j], array[i]]
    }
    return array
}




